package com.tka.inheritance_27nov;

public class For_each {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a[] = {1, 2, 3, 4, 5};

for(int i : a) {
	System.out.print(i+" ");
}
System.out.println();
for (int i = 0; i < a.length; i++) {
	System.out.print(a[i]+" ");
}
	}

}
